def parse_and_write():
    entries = []
    lines = RAW_DATA.strip().split('\n')
    
    for line in lines:
        parts = line.split('\t')
        if len(parts) >= 4:
            entry = {
                "id": f"FAQ-{parts[0]}",
                "category": parts[1],
                "question": parts[2],
                "answer": parts[3],
                # Add default options or logic to determine them if needed
                "options": [],
                "related_questions": []
            }
            entries.append(entry)
            
    # Output path
    output_path = os.path.join(os.path.dirname(os.path.dirname(__file__)), 'data/faq.json')
    
    with open(output_path, 'w', encoding='utf-8') as f:
        json.dump(entries, f, ensure_ascii=False, indent=4)
        
    print(f"Successfully wrote {len(entries)} FAQ entries to {output_path}")

if __name__ == "__main__":
    parse_and_write()
